interface PaymentState {
  payer: string;
  amount: number;
  paymentStatus: 'pending' | 'completed' | 'failed';
}

const initialState: PaymentState = {
  payer: '',
  amount: 0,
  paymentStatus: 'pending'
};

const paymentSlice = createSlice({
  name: 'payment',
  initialState,
  reducers: {
    setPaymentDetails: (state, action: PayloadAction<{ payer: string; amount: number }>) => {
      state.payer = action.payload.payer;
      state.amount = action.payload.amount;
      state.paymentStatus = 'pending';
    },
    clearPaymentDetails: (state) => {
      state.payer = '';
      state.amount = 0;
      state.paymentStatus = 'pending';
    },
    updatePaymentStatus: (state, action: PayloadAction<'pending' | 'completed' | 'failed'>) => {
      state.paymentStatus = action.payload;
    }
  }
});

export const { setPaymentDetails, clearPaymentDetails, updatePaymentStatus } = paymentSlice.actions;
export default paymentSlice.reducer;
